import React, { useState, useRef, useEffect } from "react";
import "./styles.css";

const VideoPlayer = () => {
  const [isPaused, setIsPaused] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [volume, setVolume] = useState(1);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isFullScreen, setIsFullScreen] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;

    const handleKeyDown = (e) => {
      if (e.target.tagName.toLowerCase() === "input") return;

      switch (e.key.toLowerCase()) {
        case " ":
        case "k":
          togglePlayPause();
          break;
        case "f":
          toggleFullScreen();
          break;
        case "t":
          toggleTheaterMode();
          break;
        case "m":
          toggleMute();
          break;
        case "arrowleft":
        case "j":
          skip(-5);
          break;
        case "arrowright":
        case "l":
          skip(5);
          break;
        case "c":
          toggleCaptions();
          break;
        default:
          break;
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  const togglePlayPause = () => {
    const video = videoRef.current;
    if (video.paused) {
      video.play();
    } else {
      video.pause();
    }
    setIsPaused(!video.paused);
  };

  const toggleMute = () => {
    const video = videoRef.current;
    video.muted = !video.muted;
    setIsMuted(video.muted);
  };

  const toggleFullScreen = () => {
    const video = videoRef.current;
    if (!isFullScreen) {
      video.requestFullscreen();
    } else {
      document.exitFullscreen();
    }
    setIsFullScreen(!isFullScreen);
  };

  const toggleTheaterMode = () => {
    // Implement theater mode here
  };

  const toggleCaptions = () => {
    // Implement captions toggle here
  };

  const skip = (duration) => {
    const video = videoRef.current;
    video.currentTime += duration;
  };

  const handleVolumeChange = (e) => {
    const video = videoRef.current;
    const newVolume = parseFloat(e.target.value);
    video.volume = newVolume;
    setVolume(newVolume);
    video.muted = newVolume === 0;
    setIsMuted(video.muted);
  };

  const handleTimeUpdate = () => {
    const video = videoRef.current;
    setCurrentTime(video.currentTime);
  };

  const handleDurationChange = () => {
    const video = videoRef.current;
    setDuration(video.duration);
  };

  return (
    <div className="video-container">
      <video
        ref={videoRef}
        src="/video.mp4"
        onTimeUpdate={handleTimeUpdate}
        onDurationChange={handleDurationChange}
      >
        <track kind="captions" srcLang="en" src="assets/subtitles.vtt" />
      </video>
      <div className="video-controls-container">
        {/* Play/Pause button */}
        <button className="play-pause-btn" onClick={togglePlayPause}>
          {isPaused ? (
            <svg className="play-icon" viewBox="0 0 24 24">
              <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
            </svg>
          ) : (
            <svg className="pause-icon" viewBox="0 0 24 24">
              <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
            </svg>
          )}
        </button>
        {/* Mute button */}
        <div className="volume-container">
          <button className="mute-btn" onClick={toggleMute}>
            {isMuted ? (
              <svg className="volume-muted-icon" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M12,4L9.91,6.09L12,8.18M4.27,3L3,4.27L7.73,9H3V15H7L12,20V4L7,9H3Z"
                />
              </svg>
            ) : volume > 0.5 ? (
              <svg className="volume-high-icon" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M14,3.23V5.29C16.89,6.15 19,8.83 19,12C19,15.17 16.89,17.84 14,18.7V20.77C18,19.86 21,16.28 21,12C21,7.72 18,4.14 14,3.23M16.5,12C16.5,10.23 15.5,8.71 14,7.97V16C15.5,15.29 16.5,13.76 16.5,12M3,9V15H7L12,20V4L7,9H3Z"
                />
              </svg>
            ) : (
              <svg className="volume-low-icon" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M5,9V15H9L14,20V4L9,9M18.5,12C18.5,10.23 17.5,8.71 16,7.97V16C17.5,15.29 18.5,13.76 18.5,12Z"
                />
              </svg>
            )}
          </button>
          {/* Volume slider */}
          <input
            className="volume-slider"
            type="range"
            min="0"
            max="1"
            step="any"
            value={volume}
            onChange={handleVolumeChange}
          />
        </div>
        {/* Duration */}
        <div className="duration-container">
          <div className="current-time">{formatDuration(currentTime)}</div>/
          <div className="total-time">{formatDuration(duration)}</div>
        </div>
        {/* Other controls */}
        {/* Add other controls here */}
      </div>
    </div>
  );
};

const leadingZeroFormatter = new Intl.NumberFormat(undefined, {
  minimumIntegerDigits: 2,
});

const formatDuration = (time) => {
  const seconds = Math.floor(time % 60);
  const minutes = Math.floor(time / 60) % 60;
  const hours = Math.floor(time / 3600);
  if (hours === 0) {
    return `${minutes}:${leadingZeroFormatter.format(seconds)}`;
  } else {
    return `${hours}:${leadingZeroFormatter.format(
      minutes
    )}:${leadingZeroFormatter.format(seconds)}`;
  }
};

export default VideoPlayer;
