import React, { useRef, useEffect, useState } from "react";
import Hls from "hls.js";
import "font-awesome/css/font-awesome.min.css";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);

  useEffect(() => {
    const video = videoRef.current;

    if (Hls.isSupported()) {
      const hls = new Hls();
      hlsRef.current = hls;

      hls.loadSource("http://localhost:8080/video/master.m3u8");
      hls.attachMedia(video);

      return () => {
        if (hls) {
          hls.destroy();
        }
      };
    } else if (video.canPlayType("application/vnd.apple.mpegurl")) {
      video.src = "http://localhost:8080/video/master.m3u8";
    }
  }, []);

  const togglePlay = () => {
    const video = videoRef.current;
    if (video.paused) {
      video.play();
      setIsPlaying(true);
    } else {
      video.pause();
      setIsPlaying(false);
    }
  };

  const toggleMute = () => {
    const video = videoRef.current;
    video.muted = !video.muted;
    setIsMuted(video.muted);
  };

  return (
    <div>
      {/* Width and margin for styling */}
      <video
        ref={videoRef}
        width="1280"
        height="720"
        autoPlay={false} // Disable autoplay
      />
      <div>
        <button onClick={togglePlay}>
          <i className={`fa ${isPlaying ? "fa-pause fa-fw" : "fa-play"}`} />
        </button>
        <button onClick={toggleMute}>
          <i
            className={`fa ${isMuted ? "fa-volume-off fa-fw" : "fa-volume-up"}`}
          />
        </button>
      </div>
    </div>
  );
};

export default VideoPlayer;
