import React, { useRef, useEffect, useState } from "react";
import Hls from "hls.js";
import "./styles.css";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [volume, setVolume] = useState(1); // Initialize volume to 1 (100%)

  const togglePlay = () => {
    const video = videoRef.current;
    if (video.paused) {
      video.play();
      setIsPlaying(true);
    } else {
      video.pause();
      setIsPlaying(false);
    }
  };

  const toggleMute = () => {
    const video = videoRef.current;
    video.muted = !video.muted;
    setIsMuted(video.muted);
  };

  const handleVolumeChange = (event) => {
    const video = videoRef.current;
    const volume = parseFloat(event.target.value);
    video.volume = volume;
    setVolume(volume);
    if (volume === 0) {
      video.setAttribute("data-volume-level", "muted");
    } else if (volume < 0.5) {
      video.setAttribute("data-volume-level", "low");
    } else {
      video.setAttribute("data-volume-level", "high");
    }
  };

  useEffect(() => {
    const video = videoRef.current;

    if (Hls.isSupported()) {
      const hls = new Hls();
      hlsRef.current = hls;

      hls.loadSource("http://localhost:8080/video/master.m3u8");
      hls.attachMedia(video);

      // Removed video.play() call

      return () => {
        if (hls) {
          hls.destroy();
        }
      };
    } else if (video.canPlayType("application/vnd.apple.mpegurl")) {
      video.src = "http://localhost:8080/video/master.m3u8";
      // Removed video.play() call
    }
  }, []);

  return (
    <div className="video-container" data-volume-level="high">
      <div className="video-controls-container">
        {/* Other controls */}
        <div className="controls">
          {/* Other controls */}
          <div className="volume-container">
            <button className="mute-btn" onClick={toggleMute}>
              {/* Icons for volume */}
            </button>

            <input
              className="volume-slider"
              type="range"
              min="0"
              max="1"
              step="any"
              value={volume} // Bind value to volume state
              onChange={handleVolumeChange} // Handle volume change
            />
          </div>
          {/* Other controls */}
        </div>
      </div>
      <video ref={videoRef}></video>
    </div>
  );
};

export default VideoPlayer;
