import React, { useRef, useEffect, useState, useCallback } from "react";
import Hls from "hls.js";
import "./styles.css";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [volume, setVolume] = useState(1);
  const [currentTime, setCurrentTime] = useState("0:00");
  const [totalTime, setTotalTime] = useState("0:00");
  const [progressPosition, setProgressPosition] = useState(0);

  const handleVolumeChange = useCallback((event) => {
    const newVolume = parseFloat(event.target.value);
    setVolume(newVolume);
    videoRef.current.volume = newVolume;
    videoRef.current.muted = newVolume === 0;
    setIsMuted(videoRef.current.muted);
  }, []);

  useEffect(() => {
    const video = videoRef.current;

    const updateCurrentTime = () => {
      const formattedTime = formatTime(video.currentTime);
      setCurrentTime(formattedTime);
      const percent = (video.currentTime / video.duration) * 100;
      setProgressPosition(percent);
    };

    const updateTotalTime = () => {
      const formattedTime = formatTime(video.duration);
      setTotalTime(formattedTime);
    };

    video.addEventListener("timeupdate", updateCurrentTime);
    video.addEventListener("durationchange", updateTotalTime);

    return () => {
      video.removeEventListener("timeupdate", updateCurrentTime);
      video.removeEventListener("durationchange", updateTotalTime);
    };
  }, []);

  useEffect(() => {
    const video = videoRef.current;

    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource("http://localhost:8080/video/master.m3u8");
      hls.attachMedia(video);

      return () => {
        if (hls) {
          hls.destroy();
        }
      };
    } else if (video.canPlayType("application/vnd.apple.mpegurl")) {
      video.src = "http://localhost:8080/video/master.m3u8";
    }
  }, []);

  const togglePlay = () => {
    const video = videoRef.current;
    if (video.paused) {
      video.play();
      setIsPlaying(true);
    } else {
      video.pause();
      setIsPlaying(false);
    }
  };

  const toggleMute = () => {
    const video = videoRef.current;
    video.muted = !video.muted;
    setIsMuted(video.muted);
    setVolume(video.muted ? 0 : volume);
  };

  const handleTimelineClick = (e) => {
    const timelineContainer = e.currentTarget;
    const rect = timelineContainer.getBoundingClientRect();
    const percent = (e.clientX - rect.left) / rect.width;
    const video = videoRef.current;
    video.currentTime = percent * video.duration;
  };

  const formatTime = (time) => {
    const hours = Math.floor(time / 3600);
    const minutes = Math.floor((time % 3600) / 60);
    const seconds = Math.floor(time % 60);

    const formattedHours = hours > 0 ? `${hours}:` : "";
    const formattedMinutes = `${
      minutes < 10 && hours > 0 ? "0" : ""
    }${minutes}:`;
    const formattedSeconds = `${seconds < 10 ? "0" : ""}${seconds}`;

    return `${formattedHours}${formattedMinutes}${formattedSeconds}`;
  };

  return (
    <div className="video-container paused" data-volume-level="high">
      <div className="video-controls-container">
        <div className="timeline-container" onClick={handleTimelineClick}>
          <div
            className="timeline"
            style={{ "--progress-position": `${progressPosition}%` }}
          >
            <div className="thumb-indicator"></div>
          </div>
        </div>
        <div className="controls">
          <button onClick={togglePlay}>
            {isPlaying ? (
              <svg className="icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
              </svg>
            ) : (
              <svg className="icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
              </svg>
            )}
          </button>

          <div className="volume-container">
            <button className="mute-btn" onClick={toggleMute}>
              {isMuted || volume === 0 ? (
                <svg className="volume-muted-icon" viewBox="0 0 24 24">
                  {/* Muted icon */}
                </svg>
              ) : volume >= 0.5 ? (
                <svg className="volume-high-icon" viewBox="0 0 24 24">
                  {/* High volume icon */}
                </svg>
              ) : (
                <svg className="volume-low-icon" viewBox="0 0 24 24">
                  {/* Low volume icon */}
                </svg>
              )}
            </button>

            <input
              className="volume-slider"
              type="range"
              min="0"
              max="1"
              step="any"
              value={volume}
              onChange={handleVolumeChange}
            />
          </div>
          <div className="duration-container">
            <div className="current-time">{currentTime}</div>/
            <div className="total-time">{totalTime}</div>
          </div>

          <button className="full-screen-btn">{/* Fullscreen button */}</button>
        </div>
      </div>
      <video ref={videoRef}></video>
    </div>
  );
};

export default VideoPlayer;
