import React, { useRef, useEffect, useState } from "react";
import Hls from "hls.js";
import "./styles.css";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);

  const [volumeLevel, setVolumeLevel] = useState("high");
  const [isPaused, setIsPaused] = useState(true);
  const [isFullScreen, setIsFullScreen] = useState(false);

  const togglePlayPause = () => {
    const video = videoRef.current;
    if (video.paused) {
      video.play();
    } else {
      video.pause();
    }
    setIsPaused(video.paused);
  };

  const toggleMute = () => {
    const video = videoRef.current;
    video.muted = !video.muted;
    setVolumeLevel(video.muted ? "muted" : "high");
  };

  const handleVolumeChange = (event) => {
    const video = videoRef.current;
    const volume = parseFloat(event.target.value);
    video.volume = volume;
    if (volume === 0) {
      setVolumeLevel("muted");
    } else if (volume < 0.5) {
      setVolumeLevel("low");
    } else {
      setVolumeLevel("high");
    }
  };

  const toggleFullScreen = () => {
    const video = videoRef.current;
    if (!isFullScreen) {
      if (video.requestFullscreen) {
        video.requestFullscreen();
      } else if (video.mozRequestFullScreen) {
        /* Firefox */
        video.mozRequestFullScreen();
      } else if (video.webkitRequestFullscreen) {
        /* Chrome, Safari and Opera */
        video.webkitRequestFullscreen();
      } else if (video.msRequestFullscreen) {
        /* IE/Edge */
        video.msRequestFullscreen();
      }
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      } else if (document.mozCancelFullScreen) {
        /* Firefox */
        document.mozCancelFullScreen();
      } else if (document.webkitExitFullscreen) {
        /* Chrome, Safari and Opera */
        document.webkitExitFullscreen();
      } else if (document.msExitFullscreen) {
        /* IE/Edge */
        document.msExitFullscreen();
      }
    }
    setIsFullScreen(!isFullScreen);
  };

  useEffect(() => {
    const video = videoRef.current;

    if (Hls.isSupported()) {
      const hls = new Hls();
      hlsRef.current = hls;

      hls.loadSource("http://localhost:8080/video/master.m3u8");
      hls.attachMedia(video);

      return () => {
        if (hls) {
          hls.destroy();
        }
      };
    } else if (video.canPlayType("application/vnd.apple.mpegurl")) {
      video.src = "http://localhost:8080/video/master.m3u8";
    }
  }, []);

  return (
    <div
      className={`video-container ${isPaused ? "paused" : ""}`}
      data-volume-level={volumeLevel}
    >
      <div className="video-controls-container">
        <div className="timeline-container">
          <div className="timeline"></div>
        </div>
        <div className="controls">
          <button className="play-pause-btn" onClick={togglePlayPause}>
            {isPaused ? (
              <svg className="play-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
              </svg>
            ) : (
              <svg className="pause-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
              </svg>
            )}
          </button>
          <div className="volume-container">
            <button className="mute-btn" onClick={toggleMute}>
              {volumeLevel === "muted" ? (
                <svg className="volume-muted-icon" viewBox="0 0 24 24">
                  <path
                    fill="currentColor"
                    d="M12,4L9.91,6.09L12,8.18M4.27,3L3,4.27L7.73,9H3V15H7L12,20V13.27L16.25,17.53C15.58,18.04 14.83,18.46 14,18.7V20.77C15.38,20.45 16.63,19.82 17.68,18.96L19.73,21L21,19.73L12,10.73M19,12C19,12.94 18.8,13.82 18.46,14.64L19.97,16.15C20.62,14.91 21,13.5 21,12C21,7.72 18,4.14 14,3.23V5.29C16.89,6.15 19,8.83 19,12M16.5,12C16.5,10.23 15.5,8.71 14,7.97V10.18L16.45,12.63C16.5,12.43 16.5,12.21 16.5,12Z"
                  />
                </svg>
              ) : volumeLevel === "low" ? (
                <svg className="volume-low-icon" viewBox="0 0 24 24">
                  <path
                    fill="currentColor"
                    d="M5,9V15H9L14,20V4L9,9M18.5,12C18.5,10.23 17.5,8.71 16,7.97V16C17.5,15.29 18.5,13.76 18.5,12Z"
                  />
                </svg>
              ) : (
                <svg className="volume-high-icon" viewBox="0 0 24 24">
                  <path
                    fill="currentColor"
                    d="M14,3.23V5.29C16.89,6.15 19,8.83 19,12C19,15.17 16.89,17.84 14,18.7V20.77C18,19.86 21,16.28 21,12C21,7.72 18,4.14 14,3.23M16.5,12C16.5,10.23 15.5,8.71 14,7.97V16C15.5,15.29 16.5,13.76 16.5,12M3,9V15H7L12,20V4L7,9H3Z"
                  />
                </svg>
              )}
            </button>
            <input
              className="volume-slider"
              type="range"
              min="0"
              max="1"
              step="any"
              value={videoRef.current ? videoRef.current.volume : 1}
              onChange={handleVolumeChange}
            />
          </div>
          <div className="duration-container">
            <div className="current-time">0:00</div>/
            <div className="total-time"></div>
          </div>

          <button className="mini-player-btn">
            <svg viewBox="0 0 24 24">
              <path
                fill="currentColor"
                d="M21 3H3c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h18c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H3V5h18v14zm-10-7h9v6h-9z"
              />
            </svg>
          </button>

          <button className="full-screen-btn" onClick={toggleFullScreen}>
            {isFullScreen ? (
              <svg className="close" viewBox="0 0 24 24">
                {/* SVG path for close icon */}
              </svg>
            ) : (
              <svg className="open" viewBox="0 0 24 24">
                {/* SVG path for open icon */}
              </svg>
            )}
          </button>
        </div>
      </div>
      <video ref={videoRef}></video>
    </div>
  );
};

export default VideoPlayer;
