import React, { useEffect, useRef } from "react";
import videojs from "video.js";
import "video.js/dist/video-js.css";

const VideoPlayer = ({ url }) => {
  const videoRef = useRef();

  useEffect(() => {
    // Check if the videoRef has been initialized
    if (videoRef.current) {
      // Initialize Video.js player
      const player = videojs(videoRef.current, {
        controls: true,
        autoplay: true,
        preload: "auto",
        fluid: true,
        sources: [
          {
            src: "http://192.168.1.200:3000/output/output.m3u8",
            type: "application/x-mpegURL",
          },
        ],
      });

      // Cleanup function to dispose the player when the component unmounts
      return () => {
        if (player) {
          player.dispose();
        }
      };
    }
  }, [url]); // Re-run effect when the URL changes

  return (
    <div data-vjs-player>
      {/* Render the <video> element with the provided ref */}
      <video ref={videoRef} className="video-js" />
    </div>
  );
};

export default VideoPlayer;
